import React, { useState, useEffect } from 'react';

export default function Filters() {

    const [isOpen, setIsOpen] = useState(false);

    const toggleDropdown = () => {
        setIsOpen(!isOpen);
    }

    return (
        <div className="filter-buttons py-5 relative">
            <button onClick={toggleDropdown} className="border border-solid border-gray-300  filter-btn">
                Filter by Area
            </button>
            {isOpen && (
                <div className="absolute z-10 mt-2 w-48 bg-white shadow-lg rounded border">
                    <button className="block w-full text-left px-4 py-2 text-gray-800 hover:bg-gray-200">Option 1</button>
                    <button className="block w-full text-left px-4 py-2 text-gray-800 hover:bg-gray-200">Option 2</button>
                    <button className="block w-full text-left px-4 py-2 text-gray-800 hover:bg-gray-200">Option 3</button>
                </div>
            )}
            <button className="border border-solid border-gray-300  filter-btn">
                Sort By
            </button>
            <button className="border border-solid border-gray-300  filter-btn">
                Sort By
            </button>
            <button className="border border-solid border-gray-300  filter-btn">
                Fast Delivery
            </button>
            <button className="border border-solid border-gray-300  filter-btn">
                Ratings 4.0+
            </button>
            <button className="border border-solid border-gray-300  filter-btn">
                Offers
            </button>
        </div>
    )
}
