// import React from "react";

import React, { useState } from 'react';


export default function Filters() {

    const [isOpen, setIsOpen] = useState(false);

    const toggleDropdown = () => {
        setIsOpen(!isOpen);
    };

    return (
        <div className="my-5">
            <h2 className="antialiased font-extrabold tracking-tighter text-2xl">Restaurant with online food delivery in Pune</h2>
            <div className="filter-buttons py-5">

                <button className="border border-solid border-gray-300  filter-btn">
                    Filter
                    <svg className="w-4 h-4 ml-2" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor">
                        <path fill-rule="evenodd" d="M4 4a1 1 0 011-1h10a1 1 0 011 1v1a1 1 0 01-1 1H5a1 1 0 01-1-1V4zm7 4a1 1 0 100 2 1 1 0 000-2zM5 14a1 1 0 100 2h10a1 1 0 100-2H5z" clip-rule="evenodd" />
                    </svg>
                </button>
                <button className="border border-solid border-gray-300  filter-btn">
                    Sort By
                </button>
                <button className="border border-solid border-gray-300  filter-btn">
                    Sort By
                </button>
                <button className="border border-solid border-gray-300  filter-btn">
                    Fast Delivery
                </button>
                <button className="border border-solid border-gray-300  filter-btn">
                    Ratings 4.0+
                </button>
                <button className="border border-solid border-gray-300  filter-btn">
                    Offers
                </button>
            </div>
        </div>
    )
}
