import React, { useState, useEffect } from 'react';

const FilterButtons = () => {
    const [meals, setMeals] = useState([]);
    const [selectedArea, setSelectedArea] = useState('');

    useEffect(() => {
        const fetchData = async () => {
            if (!selectedArea) return; // Return if no area is selected
            try {
                const response = await fetch(`https://www.themealdb.com/api/json/v1/1/filter.php?a=${selectedArea}`);
                if (!response.ok) {
                    throw new Error('Failed to fetch meals');
                }
                const data = await response.json();
                setMeals(data.meals);
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        };
        fetchData();
    }, [selectedArea]);

    const handleAreaSelect = (area) => {
        setSelectedArea(area);
    };

    return (
        <div className="container mx-auto">
            <div className="flex justify-between items-center py-5">
                <div className="flex space-x-4">
                    <div className="relative">
                        <button onClick={() => handleAreaSelect('Indian')} className="border border-solid border-gray-300 px-4 py-2 rounded filter-btn">Indian</button>
                        {/* Dropdown menu */}
                        {selectedArea && (
                            <div className="absolute top-full left-0 w-48 bg-white shadow-lg rounded mt-2">
                                <button onClick={() => handleAreaSelect('Indian')} className="block w-full text-left px-4 py-2 text-gray-800 hover:bg-gray-200">Indian</button>
                                <button onClick={() => handleAreaSelect('Chinese')} className="block w-full text-left px-4 py-2 text-gray-800 hover:bg-gray-200">Chinese</button>
                                <button onClick={() => handleAreaSelect('Italian')} className="block w-full text-left px-4 py-2 text-gray-800 hover:bg-gray-200">Italian</button>
                                {/* Add more options as needed */}
                            </div>
                        )}
                    </div>
                    <button className="border border-solid border-gray-300 px-4 py-2 rounded filter-btn">
                        Sort By
                    </button>
                    <button className="border border-solid border-gray-300 px-4 py-2 rounded filter-btn">
                        Fast Delivery
                    </button>
                    <button className="border border-solid border-gray-300 px-4 py-2 rounded filter-btn">
                        Ratings 4.0+
                    </button>
                    <button className="border border-solid border-gray-300 px-4 py-2 rounded filter-btn">
                        Offers
                    </button>
                </div>
            </div>
            <div className="grid grid-cols-3 gap-4">
                {meals.map((meal, index) => (
                    <div key={index} className="border border-solid border-gray-300 rounded p-4">
                        <img src={meal.strMealThumb} alt={meal.strMeal} className="w-full h-32 object-cover mb-2" />
                        <div className="text-lg font-semibold mb-1">{meal.strMeal}</div>
                        <div className="text-sm text-gray-600">{meal.strArea}</div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default FilterButtons;
