import React, { useState, useEffect } from 'react';

export default function Filters() {

    const [isOpen, setIsOpen] = useState(false);
    const [areas, setAreas] = useState([]);
    const [selectedArea, setSelectedArea] = useState('');
    const [meals, setMeals] = useState([]);

    const toggleDropdown = () => {
        setIsOpen(!isOpen);
    }

    useEffect(() => {
        fetch('https://www.themealdb.com/api/json/v1/1/list.php?a=list')
            .then(response => response.json())
            .then(
                data => {
                    setAreas(data.meals)
                }
            )
            .catch(error => {
                console.log('Error fetching drop down items');
            });
    }, []);

    useEffect(() => {
        if (selectedArea) {

            fetch(`https://www.themealdb.com/api/json/v1/1/filter.php?a=${selectedArea}`)
                .then(response => response.json())
                .then(
                    data => {
                        // setMeals(data.meals)
                        const mealPromises = data.meals.map(async meal => {
                            const categoryResponse = await fetch(`https://www.themealdb.com/api/json/v1/1/lookup.php?i=${meal.idMeal}`);
                            const categoryData = await categoryResponse.json();
                            return { ...meal, category: categoryData.meals[0].strCategory };
                        });
                        Promise.all(mealPromises).then(mealsWithCategory => setMeals(mealsWithCategory));
                    }
                )
                .catch(error => {
                    console.log('Error fetching meals');
                })
        }
    }, [selectedArea]);

    useEffect(() => {
        fetch(`https://www.themealdb.com/api/json/v1/1/filter.php?a=Indian`)
            .then(response => response.json())
            .then(
                data => {
                    // setMeals(data.meals)
                    const mealPromises = data.meals.map(async meal => {
                        const categoryResponse = await fetch(`https://www.themealdb.com/api/json/v1/1/lookup.php?i=${meal.idMeal}`);
                        const categoryData = await categoryResponse.json();
                        return { ...meal, category: categoryData.meals[0].strCategory };
                    });
                    Promise.all(mealPromises).then(mealsWithCategory => setMeals(mealsWithCategory));

                }
            )
            .catch(error => {
                console.log('Error fetching Indian , meals', error);
            })
    }, []);

    const handleAreaSelection = (area) => {
        setSelectedArea(area);
        setIsOpen(false);
    }

    return (
        <div className='py-5'>
            <h2 className='antialiased font-bold text-2xl break-words pb-2.5'>Restaurants with online food delivery in Pune</h2>
            <div className="filter-buttons relative">
                <button onClick={toggleDropdown} className="border border-solid border-gray-300  filter-btn">
                    {selectedArea ? `Filter by ${selectedArea}` : "Filter by Area"}
                </button>

                {isOpen && (
                    < div className="absolute z-10 mt-2 w-100 bg-white shadow-lg rounded border grid grid-cols-3 ">
                        {areas.map(area => (
                            <button className="block w-full text-left px-4 py-2 text-gray-800 hover:bg-gray-200" onClick={() => handleAreaSelection(area.strArea)}>
                                {area.strArea}
                            </button>
                        ))}
                    </div>
                )
                }
                <button className="border border-solid border-gray-300  filter-btn">
                    Sort By
                </button>
                <button className="border border-solid border-gray-300  filter-btn">
                    Sort By
                </button>
                <button className="border border-solid border-gray-300  filter-btn">
                    Fast Delivery
                </button>
                <button className="border border-solid border-gray-300  filter-btn">
                    Ratings 4.0+
                </button>
                <button className="border border-solid border-gray-300  filter-btn">
                    Offers
                </button>
            </div >
            <div className="grid grid-cols-4 gap-8 py-5">
                {meals !== null && (
                    meals.map(meal => (
                        <div key={meal.idMeal}>
                            <div class="meals-image rounded-2xl" >
                                <img className="object-cover" src={meal.strMealThumb} alt={meal.strMeal} />
                            </div>
                            <div className="meals-details py-3 ml-3">
                                <h3 className='antialiased font-bold text-lg break-words'> {meal.strMeal}</h3>
                                <div className="meals-rating">
                                    <svg width="20" height="20" viewBox="0 0 20 20" fill="none" role="img" aria-hidden="true">
                                        <circle cx="10" cy="10" r="9" fill="url(#paint0_linear)"></circle>
                                        <path d="M10.0816 12.865C10.0312 12.8353 9.96876 12.8353 9.91839 12.865L7.31647 14.3968C6.93482 14.6214 6.47106 14.2757 6.57745 13.8458L7.27568 11.0245C7.29055 10.9644 7.26965 10.9012 7.22195 10.8618L4.95521 8.99028C4.60833 8.70388 4.78653 8.14085 5.23502 8.10619L8.23448 7.87442C8.29403 7.86982 8.34612 7.83261 8.36979 7.77777L9.54092 5.06385C9.71462 4.66132 10.2854 4.66132 10.4591 5.06385L11.6302 7.77777C11.6539 7.83261 11.706 7.86982 11.7655 7.87442L14.765 8.10619C15.2135 8.14085 15.3917 8.70388 15.0448 8.99028L12.7781 10.8618C12.7303 10.9012 12.7095 10.9644 12.7243 11.0245L13.4225 13.8458C13.5289 14.2757 13.0652 14.6214 12.6835 14.3968L10.0816 12.865Z" fill="white"></path>
                                        <defs>
                                            <linearGradient id="paint0_linear" x1="10" y1="1" x2="10" y2="19" gradientUnits="userSpaceOnUse">
                                                <stop stop-color="#21973B"></stop>
                                                <stop offset="1" stop-color="#128540"></stop>
                                            </linearGradient>
                                        </defs>
                                    </svg>

                                </div>
                                <p className="text-gray-500 mt-1">{meal.category}</p>
                            </div>
                        </div>
                    ))

                )}


            </div>
        </div >
    )
}
