import React, { useState, useEffect } from 'react';

const FilterButtons = () => {
    const [meals, setMeals] = useState([]);
    const [filterOptions, setFilterOptions] = useState([]);
    const [selectedFilter, setSelectedFilter] = useState('');
    const [showDropdown, setShowDropdown] = useState(false);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await fetch(`https://www.themealdb.com/api/json/v1/1/filter.php?a=${selectedFilter}`);
                if (!response.ok) {
                    throw new Error('Failed to fetch meals');
                }
                const data = await response.json();
                setMeals(data.meals);
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        };

        if (selectedFilter) {
            fetchData();
        }
    }, [selectedFilter]);

    useEffect(() => {
        const fetchFilterOptions = async () => {
            try {
                const response = await fetch('https://www.themealdb.com/api/json/v1/1/list.php?a=list');
                if (!response.ok) {
                    throw new Error('Failed to fetch filter options');
                }
                const data = await response.json();
                setFilterOptions(data.meals.map(meal => meal.strArea));
            } catch (error) {
                console.error('Error fetching filter options:', error);
            }
        };

        fetchFilterOptions();
    }, []);

    const handleFilterSelect = (option) => {
        setSelectedFilter(option);
        setShowDropdown(false);
    };

    const toggleDropdown = () => {
        setShowDropdown(!showDropdown);
    };

    return (
        <div className="container mx-auto">
            <div className="flex justify-between items-center py-5">
                <div className="relative">
                    <button onClick={toggleDropdown} className="border border-solid border-gray-300 px-4 py-2 rounded filter-btn">
                        Filter by {selectedFilter || 'Area'}
                        <svg className="w-4 h-4 ml-2" viewBox="0 0 20 20" fill="currentColor">
                            <path fillRule="evenodd" d="M4 4a1 1 0 011-1h10a1 1 0 011 1v1a1 1 0 01-1 1H5a1 1 0 01-1-1V4zm7 4a1 1 0 100 2 1 1 0 000-2zM5 14a1 1 0 100 2h10a1 1 0 100-2H5z" clipRule="evenodd" />
                        </svg>
                    </button>
                    {showDropdown && (
                        <div className="absolute z-10 mt-2 w-48 bg-white shadow-lg rounded border">
                            {filterOptions.map((option, index) => (
                                <button key={index} onClick={() => handleFilterSelect(option)} className="block w-full text-left px-4 py-2 text-gray-800 hover:bg-gray-200">
                                    {option}
                                </button>
                            ))}
                        </div>
                    )}
                </div>
                <button className="border border-solid border-gray-300 px-4 py-2 rounded filter-btn">
                    Sort By
                </button>
                <button className="border border-solid border-gray-300 px-4 py-2 rounded filter-btn">
                    Fast Delivery
                </button>
                <button className="border border-solid border-gray-300 px-4 py-2 rounded filter-btn">
                    Ratings 4.0+
                </button>
                <button className="border border-solid border-gray-300 px-4 py-2 rounded filter-btn">
                    Offers
                </button>
            </div>
            <div className="grid grid-cols-3 gap-4">
                {meals.map((meal, index) => (
                    <div key={index} className="border border-solid border-gray-300 rounded p-4">
                        <img src={meal.strMealThumb} alt={meal.strMeal} className="w-full h-32 object-cover mb-2" />
                        <div className="text-lg font-semibold mb-1">{meal.strMeal}</div>
                        <div className="text-sm text-gray-600">{meal.strArea}</div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default FilterButtons;
