import React, { useState, useEffect } from 'react';

const FilterButtons = () => {
    const [showDropdown, setShowDropdown] = useState(false);
    const [areas, setAreas] = useState([]);
    const [selectedArea, setSelectedArea] = useState('Indian');

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await fetch('https://www.themealdb.com/api/json/v1/1/list.php?a=list');
                if (!response.ok) {
                    throw new Error('Failed to fetch areas');
                }
                const data = await response.json();
                setAreas(data.meals);
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        };
        fetchData();
    }, []);

    const toggleDropdown = () => {
        setShowDropdown(!showDropdown);
    };

    const handleAreaSelect = (area) => {
        setSelectedArea(area);
        setShowDropdown(false);
    };

    const filteredAreas = areas.filter(area => area.strArea === selectedArea);

    return (
        <div className="filter-buttons py-5 relative">
            <button onClick={toggleDropdown} className="border border-solid border-gray-300 px-12 py-3 rounded flex items-center">
                Filter by {selectedArea}
                <svg className="w-4 h-4 ml-2" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor">
                    <path fillRule="evenodd" d="M4 4a1 1 0 011-1h10a1 1 0 011 1v1a1 1 0 01-1 1H5a1 1 0 01-1-1V4zm7 4a1 1 0 100 2 1 1 0 000-2zM5 14a1 1 0 100 2h10a1 1 0 100-2H5z" clipRule="evenodd" />
                </svg>
            </button>
            {showDropdown && (
                <div className="absolute z-10 mt-2 w-48 bg-white shadow-lg rounded border">
                    {areas.map((area, index) => (
                        <button key={index} className="block w-full text-left px-4 py-2 text-gray-800 hover:bg-gray-200" onClick={() => handleAreaSelect(area.strArea)}>
                            {area.strArea}
                        </button>
                    ))}
                </div>
            )}
            <div className="mt-2">
                {filteredAreas.map((area, index) => (
                    <div key={index} className="px-4 py-2 border border-solid border-gray-300 rounded mt-2">
                        {area.strArea}
                    </div>
                ))}
            </div>
        </div>
    );
};

export default FilterButtons;
